const pad2 = (num: number) => num.toString().padStart(2, '0');

const formatTime = (ms: number) => `${pad2(Math.floor(ms / 3600000))}:${pad2(Math.floor((ms % 3600000) / 60000))}:${pad2(Math.floor((ms % 60000) / 1000))}`;

it('should render correctly', async () => {
    const router = createBrowserRouter([{
        path: "/",
        element: <Outlet context={{ podcast }} />,
        children: [
            {
                path: "",
                element: <PodcastEpisodeList />
            }
        ]
    }]);
    const { asFragment, getByTestId } = render(<RouterProvider router={router} />);

    for (const [episodeId, episode] of Object.entries(podcast.episodesById)) {
        const episodeElement = getByTestId(`podcast-episode-${episodeId}`);
        
        expect(getByTestIdJest(episodeElement, "podcast-episode-link").getAttribute('href')).toBe(`/episode/${episodeId}`);
        expect(getByTestIdJest(episodeElement, "podcast-episode-link").textContent).toBe(episode.title);
        expect(getByTestIdJest(episodeElement, "podcast-episode-release-date").textContent).toBe(new Date(episode.releaseDate).toLocaleDateString());
        expect(getByTestIdJest(episodeElement, "podcast-episode-length").textContent).toBe(formatTime(episode.length));
    }

    expect(asFragment()).toMatchSnapshot();
});
